def buy():
    nonce = web3.eth.get_transaction_count(api_service.get_sender_address())

    tokenToBuy = web3.toChecksumAddress('0x26734add0650719ea29087fe5cc0aab81b4f237d')
    tokenBalance = api_service.get_address_balance(56, pool_address, 'STEMX')
    tokensForBuy = float(abs(tokenBalance - baseTokens)) * 0.2
    busdForBuy = tokensForBuy * float(api_service.get_price_hard())

    buy_functions = contract.functions.swapExactTokensForTokens(
        web3.toWei(busdForBuy, 'ether'),
        1,
        [spend, tokenToBuy],
        api_service.get_sender_address(),
        (int(time.time()) + 100000)
    ).buildTransaction({
        'gas': 350000,
        'gasPrice': web3.toWei('9', 'gwei'),
        'nonce': nonce,
    })

    signed_txn = web3.eth.account.sign_transaction(buy_functions, private_key=api_service.get_private_key())
    tx_token = web3.eth.send_raw_transaction(signed_txn.rawTransaction)
    logger.log(f'Token is bought. Transaction hash: {web3.toHex(tx_token)}')


